    using ECDSA for bytes32;

    event NFTFused(
        address indexed _sender,
        uint256 assetQuantity,
        string _assetsId,
        uint256[] _tokenIds,
        uint256[] _tokenAmounts,
        uint256 rERC20TokenAmount
